import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 1280px;
  `;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;

`;

export const InfoAboutBlurredContainer = styled.div`
position: absolute;
  width: 499px;
  height: 450px;
  z-index: 1;
top: 171px;
left: 132px;
box-shadow: inset 0px 0px 2000px rgba(0, 0, 0, .8);

`;

export const InfoAboutBlurred = styled.img`
position: absolute;

width: 100%;
`;
