import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 1280px;
  `;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;

`;

export const InfoAboutBlurredContainer = styled.div`
position: absolute;
  width: 499px;
  height: 450px;
  z-index: 1;
top: 171px;
left: 132px;
background: inherit;
`;

export const InfoAboutBlurred = styled.img`
position: absolute;
background: inherit;
background-size: 100vw 100vw;
box-shadow: inset 0 0 2000px 5px rgba(0, 0, 0, .8);
`;
