import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 1280px;
  background-attachment: fixed;
  `;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;

`;

export const InfoAboutBlurredContainer = styled.div`
width: 499px;
  height: 370px;
position: relative;
content: "";
z-index: 2;
  
top: 171px;
left: 132px;



`;

export const InfoAboutBlurred = styled.img`
position: absolute;
width: 100%;
top: 0;
bottom: 0;
right: 0;
left: 0;
z-index: 1
height: 100%;
box-shadow: 0.2px 0.2px  rgba(0, 0, 0, .8), inset 0 0 2000px rgba(0, 0, 0, 1);
filter: blur(20px)
 
`;
