import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 100%;
  position: relative;
  `;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;
height: 100%;
bottom: 0;
object-fit: fill;







`;

export const InfoAboutBlurredContainer = styled.div`

width: 499px;
  height: 370px;
position: relative;
z-index: 2;
  
top: 171px;
left: 132px;
box-shadow: inset 0 0 1000000000px rgba(0, 0, 0, .6);



`;

export const InfoAboutBlurred = styled.img`
position: absolute;
width: 100%;
top: 0;
bottom: 0;
right: 0;
left: 0;
z-index: 1;
height: 100%;

 
`;

export const Polygon = styled.img`
width: 100%;
height: 50px;
position: absolute;
bottom: 0;

`;
