import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 1280px;
  `;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;

`;

export const InfoAboutBlurredContainer = styled.div`
position: absolute;
z-index: 2;
  width: 499px;
  height: 370px;
top: 171px;
left: 132px;
filter: blur(30px);


`;

export const InfoAboutBlurred = styled.img`
position: relative;
width: 100%;
z-index: 1
height: 100%;
box-shadow:  2px 2px  rgba(0, 0, 0, .8), inset 0px 2px  rgba(0, 0, 0, 1); 
`;
