import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 100%;
  position: relative;
  `;

export const HeaderImageContainer = styled.div`
  display: inline-block;
  overflow: hidden;
`;

export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;
width: 100%;
height: 100%;
vertical-align: middle;








`;

export const InfoAboutBlurredContainer = styled.div`

width: 499px;
  height: 370px;
position: relative;
z-index: 2;
  
top: 171px;
left: 132px;
box-shadow: inset 0 0 1000000000px rgba(0, 0, 0, .6);



`;

export const InfoAboutBlurred = styled.img`
position: absolute;
width: 100%;
top: 0;
bottom: 0;
right: 0;
left: 0;
z-index: 1;
height: 100%;

 
`;

export const Polygon = styled.div`
width: 404px;
height: 50px;
position: absolute;
bottom: 0;
background: rgba(120, 110, 109, 0.3);

`;

export const Triangle = styled.div`
position: absolute;

bottom: 0;
left: 404px;
width: 0;
      height: 0;
      border-top: px solid transparent;
      border-left: 50px solid red;
      border-bottom: 30px solid transparent;

`;
