import styled from 'styled-components';


export const NavigationBar = styled.nav`
 font-family: 'Lato', sans-serif;
  display: flex;
  flex-direction: row;
  position: absolute;
  top: 0;
  width: 100%;
  height: 20%;
  .home {
    font-size: 3.3em;
  color: white;
  margin-left: 5.3%;
  margin-top: -3%;;
  width: 350px; 
  height: 25px; 
  }

  .uList {
    display: flex:
    justify-content: right;
    width: 40%;
  height: 15%;
  float: right;
  margin-right: 4%;

  li {
    list-style: none;
  color: white;
  padding: 20px;
  width: 33%;
  }
  }
  
`;





export const SearchLiImage = styled.li`
  padding: 20px;
color: white;
  width: 100px;
  height: 100px;
`;
