import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 100%;
  position: relative;
  `;



export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;
width: 100%;
height: 100%;
object-fit: fill;








`;

export const ThumbsAndBlurredContainer = styled.div`
width: 500px;
  height: 370px;
position: absolute;
z-index: 1;
  
top: 170px;
left: 132px;
box-shadow: inset 5px 5px 2000px rgba(0, 0, 0, .9);

`;

export const InfoAboutBlurredContainer = styled.div`
position: absolute;
z-index: 2;

  font-family: 'Lato', sans-serif;
  color: white;
  text-align: justify;
  padding: 20px;
  .opinion {
    margin: 2%;
  }

  .title {
    margin-top: -7%;
    margin-bottom: 0;
    font-family: sans-serif;
    font-style: cursive;
    font-size: 3em;
  }
  .paragraph {
    margin-top: 0;
    text-align: left-center;
    font-size: 1.4rem;
  }
  img {
    position: absolute;
    bottom: 15%;
    left: 4%;
    
  }
  a {
    position: absolute;
    font-size: 0.7em;
    text-decoration: underlined;
    color: white;
    left: 9%;
    bottom: 30%;
  }
  
  h3 {
    position: absolute;
    color: white;
    bottom: 5%;
  }

  
`;


export const ThumbsRectangle = styled.div`
display: flex;
position: absolute;
bottom: -80px;
width: 250px;
  height: 80px;
  background: ${props => props.blue ? 'rgba(44, 187, 180, .8)' : 'rgba(255, 181, 51, .8)'};
  margin-left: ${props => props.blue ? 0 : '250px'};
justify-content: center;
align-items: center;  


`;

export const Thumb = styled.img`
width: 36px;
height: 37px;
align-items: center;


  

  
`;





export const Polygon = styled.div`

justify-content: right;
align-items: center;
width: 404px;
height: 50px;
position: absolute;
bottom: 0;
background: rgba(120, 110, 109, 0.3);
p {
  color: white;
  float: right;
  margin-right: 9px;

}

`;

export const Triangle = styled.div`
position: absolute;

bottom: 1.8%;
left: 404px;
width: 0;
      height: 0;
      border-top: 10px solid transparent;
      border-left: 15px solid rgba(120, 110, 109, 0.3);
      border-bottom: 10px solid transparent;
      
`;


export const PolygonText = styled.div`
  position: absolute;
  color: white;
  text-transform: capitalize;
`;
