import styled from 'styled-components';
import { device } from '../../media-queries';


export const Page = styled.div`
  margin: auto;
  font-family: 'Lato', sans-serif;
  text-align-center;

  @media ${device.laptop} {
    max-width: 800px;
  }

  @media ${device.desktop} {
    max-width: 1400px;
  }
`;
