import styled from 'styled-components';

export const Header = styled.header`

  height: 740px;
  width: 100%;
  position: relative;
  `;



export const StyledHeaderImage = styled.img`
z-index: 0;
position: absolute;
width: 100%;
height: 100%;
object-fit: fill;








`;

export const ThumbsAndBlurredContainer = styled.div`
width: 500px;
  height: 370px;
position: absolute;
z-index: 2;
  
top: 170px;
left: 132px;
box-shadow: inset 0 0 1000000000px rgba(0, 0, 0, .6);
`;

export const InfoAboutBlurredContainer = styled.div`


  font-family: 'Lato', sans-serif;
  color: white;
  text-align: justify;
  padding: 20px;
  a {
    margin-left: 20%;
    font-size: 0.7em;
    text-decoration: none;
  }






`;


export const ThumbsRectangle = styled.div`
display: flex;
position: absolute;
bottom: -80px;
width: 250px;
  height: 80px;
  background: ${props => props.blue ? 'rgb(44, 187, 180)' : 'rgb(255, 181, 51)'};
  margin-left: ${props => props.blue ? 0 : '250px'};
justify-content: center;
align-items: center;  


`;

export const Thumb = styled.img`
width: 36px;
height: 37px;
align-items: center;


  

  
`;





export const Polygon = styled.div`
display: flex;
justify-content: right;
align-items: center;
width: 404px;
height: 50px;
position: absolute;
bottom: 0;
background: rgba(120, 110, 109, 0.3);
P {
  COLOR
}

`;

export const Triangle = styled.div`
position: absolute;

bottom: 1.8%;
left: 404px;
width: 0;
      height: 0;
      border-top: 10px solid transparent;
      border-left: 15px solid rgba(120, 110, 109, 0.3);
      border-bottom: 10px solid transparent;
      
`;


export const PolygonText = styled.div`
  position: absolute;
  color: white;
  text-transform: capitalize;
`;
