import React from 'react';
import { NavigationItem } from './navigation-item/NavigationItem';

export const NavigationItems = ({ link, isUserLoggedIn }) => {
  return (
    <ul>

      <NavigationItem
        link={ link }
        exact
      >
        Burger Builder
      </NavigationItem>
      {isUserLoggedIn ?
        <NavigationItem
          link={ link }
        >
          Orders
      </NavigationItem>
        : null}
      {isUser ?
        <NavigationItem
          link='/auth'
        >
          Authenticate
      </NavigationItem>
        : <NavigationItem
          link='/logout'
        >
          Logout
      </NavigationItem>}

    </ul>
  );
}
